def getSublists(L, n):
    '''
    get all sublists of L with n in length
    the sublist must in successive index order of L
    Input: 
        L: list 
        n: length of sublist
    Output:
        list of sublists
    '''
    lst_sublsts = []
    for idx in range(len(L)+1 - n):
        sublst = []
        for cnt in range(n):
            sublst.append(L[idx+cnt])
        lst_sublsts.append(sublst)

    return lst_sublsts

